let arr=["crash","kick-bass","snare","tom-1","tom-2","tom-3","tom-4"],arrLetters=["w","a","s","d","j","k","l"];function makeSound(e){let n=new Audio("sounds/"+arr[e]+".mp3");n.currentTime=0,n.play()}for(let i=0;i<$(".drum").length;i++)$(".drum:eq("+i+")").on("click",function(){makeSound(i),buttonAnimation(arrLetters[i])});function buttonAnimation(e){$("."+e).addClass("pressed"),setTimeout(function(){$("."+e).removeClass("pressed")},100)}document.addEventListener("keydown",function(e){if (e.repeat)return;let n=arrLetters.indexOf(e.key);-1!==n&&(makeSound(n),buttonAnimation(e.key))});