let arr=["crash","kick-bass","snare","tom-1","tom-2","tom-3","tom-4"],arrLetters=["w","a","s","d","j","k","l"];function makeSound(n){new Audio("sounds/"+arr[n]+".mp3").play()}for(let i=0;i<$(".drum").length;i++)$(".drum:eq("+i+")").on("click",function(){makeSound(i),buttonAnimation(arrLetters[i])});function buttonAnimation(n){$("."+n).addClass("pressed"),setTimeout(function(){$("."+n).removeClass("pressed")},100)}document.addEventListener("keydown",function(n){let t=arrLetters.indexOf(n.key);-1!==t&&(makeSound(t),buttonAnimation(n.key))});